css_content:
body {
  font-family: "Segoe UI", system-ui, sans-serif;
  background: #ffffff;
  color: #333;
}
/* HEADER */
.invoice-header {
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  padding: 40px;
  border-radius: 16px;
  margin-bottom: 30px;
}
.invoice-header h1 {
  font-size: 32px;
  margin-bottom: 10px;
}
.invoice-header h2 {
  font-size: 40px;
  text-align: right;
}
/* BILL / SHIP */
.parties {
  display: flex;
  justify-content: space-between;
  gap: 40px;
  margin-bottom: 30px;
  padding-bottom: 25px;
  border-bottom: 2px solid #eee;
}
.parties h3 {
  color: #667eea;
  text-transform: uppercase;
  font-size: 14px;
}
/* META TABLE */
.meta table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 30px;
}
.meta th {
  background: #667eea;
  color: white;
  padding: 12px;
}
.meta td {
  background: #f8f9ff;
  padding: 12px;
  text-align: center;
}
/* ITEMS TABLE */
table {
  width: 100%;
  border-collapse: collapse;
  margin-bottom: 30px;
}
table th {
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  padding: 14px;
}
table td {
  border-bottom: 1px solid #eee;
  padding: 14px;
}
table td:last-child,
table th:last-child {
  text-align: right;
}
/* TOTALS */
.totals {
  width: 350px;
  margin-left: auto;
  margin-top: 20px;
  padding: 20px;
  background: #f8f9ff;
  border-radius: 12px;
}
.totals h3 {
  background: linear-gradient(135deg, #667eea, #764ba2);
  color: white;
  padding: 15px;
  border-radius: 10px;
  text-align: center;
}
/* NOTES */
.notes {
  margin-top: 40px;
  padding: 20px;
  background: #fffbf0;
  border-left: 5px solid #ffb300;
  border-radius: 10px;
}
/* FOOTER */
.footer {
  text-align: center;
  margin-top: 40px;
  padding: 20px;
  background: #f8f9ff;
  font-size: 13px;
  color: #666;
}
